Decorator & Convention

public class Factory 
{
    private Func constructor;
    public Factory(Func constructor)
    {
        this.constructor = constructor;
    }
    public T Create(IResolver resolver) 
    {
        return constructor(resolver);
    }
}
public class Convention
{
    private Action apply;
    public Convention(Action apply)
    {
        this.apply = apply;
    }
    public void Apply(T t)
    {
        apply(t);
    }
}
public static class ConventionExtensions 
{
    public static IRegistyModel Register(this IRegister register,Func constructor)
    {
        return register.CreateModel(constructor);
    }

    public static IRegister UseConvention(this IRegistryModel model,Action convention) 
    {
        return model.register.Add(resolver => {
            var result = model.constructor(resolver);
            convention(result);
            return result;
        };
    }
}
public class Runner
{
    public void Main()
    {
        var expectstr = "test";
        var expectint = 2;
        var container = (new Register() as IRegister)
            .Register>().UseConvention(x => x.Value = expectstr)
            .Register(ModelA)().UseConvention(x => x.Value = expectint)
            .Build();

        Assert.Equal(expectstr,container.Resolve>());
        Assert.Equal(expectint,container.Resolve>());
    }
}
public class ModelA
{
    T Value { get; set; }
}

